INSERT INTO tb_unidade_Matematica (nome, descricao) VALUES ('Números','Desenvolver a noção de número, incluindo as ideias de aproximação, proporcionalidade, equivalência e ordem');
INSERT INTO tb_unidade_Matematica (nome, descricao) VALUES ('Álgebra','Desenvolver as ideias de equivalência, proporcionalidade, variação e interdependência');
INSERT INTO tb_unidade_Matematica (nome, descricao) VALUES ('Geometria','Estudar algoritmos e fluxogramas para resolver problemas geométricos');
INSERT INTO tb_unidade_Matematica (nome, descricao) VALUES ('Grandezas e medidas','Consolidar e ampliar a noção de número, aplicar noções geométricas e estruturar o pensamento algébrico');
INSERT INTO tb_unidade_Matematica (nome, descricao) VALUES ('Probabilidade e estatística','Resolver problemas envolvendo a comparação e equivalência de valores monetários, identificar resultados possíveis, estimar probabilidades, ler e interpretar dados apresentados em tabelas e gráficos');
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 6, 'EF06MA01', 'Comparar, ordenar, ler e escrever números naturais e números racionais cuja representação decimal é finita, fazendo uso da reta numérica.','Sistema de numeração decimal: características, leitura, escrita e comparação de números naturais e de números racionais representados na forma decimal', 1);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 6, 'EF06MA02', 'Reconhecer o sistema de numeração decimal, como o que prevaleceu no mundo ocidental, e destacar semelhanças e diferenças com outros sistemas, de modo a sistematizar suas principais características (base, valor posicional e função do zero), utilizando, inclusive, a composição e decomposição de números naturais e números racionais em sua representação decimal.','Sistema de numeração decimal: características, leitura, escrita e comparação de números naturais e de números racionais representados na forma decimal', 1);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 6, 'EF06MA03', 'Resolver e elaborar problemas que envolvam cálculos (mentais ou escritos, exatos ou aproximados) com números naturais, por meio de estratégias variadas, com compreensão dos processos neles envolvidos com e sem uso de calculadora.','Operações (adição, subtração, multiplicação, divisão e potenciação) com números naturais. Divisão euclidiana', 1);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 6, 'EF06MA04', 'Construir algoritmo em linguagem natural e representá-lo por fluxograma que indique a resolução de um problema simples (por exemplo, se um número natural qualquer é par).', 'Fluxograma para determinar a paridade de um número natural. Múltiplos e divisores de um número natural.  Números primos e compostos', 1);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 6, 'EF06MA05', 'Classificar números naturais em primos e compostos, estabelecer relações entre números, expressas pelos termos “é múltiplo de”, “é divisor de”, “é fator de”, e estabelecer, por meio de investigações, critérios de divisibilidade por 2, 3, 4, 5, 6, 8, 9, 10, 100 e 1000.', 'Fluxograma para determinar a paridade de um número natural. Múltiplos e divisores de um número natural.  Números primos e compostos', 1);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 6, 'EF06MA06', 'Resolver e elaborar problemas que envolvam as ideias de múltiplo e de divisor.', 'Fluxograma para determinar a paridade de um número natural. Múltiplos e divisores de um número natural.  Números primos e compostos', 1);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 6, 'EF06MA07', 'Compreender, comparar e ordenar frações associadas às ideias de partes de inteiros e resultado de divisão, identificando frações equivalentes.', 'Frações: significados (parte/todo, quociente), equivalência, comparação, adição e subtração; cálculo da fração de um número natural; adição e subtração de frações', 3);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 6, 'EF06MA08', 'Reconhecer que os números racionais positivos podem ser expressos nas formas fracionária e decimal, estabelecer relações entre essas representações, passando de uma representação para outra, e relacionálos a pontos na reta numérica.', 'Frações: significados (parte/todo, quociente), equivalência, comparação, adição e subtração; cálculo da fração de um número natural; adição e subtração de frações', 1);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 6, 'EF06MA09', 'Resolver e elaborar problemas que envolvam o cálculo da fração de uma quantidade e cujo resultado seja um número natural, com e sem uso de calculadora.', 'Frações: significados (parte/todo, quociente), equivalência, comparação, adição e subtração; cálculo da fração de um número natural; adição e subtração de frações', 3);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 6, 'EF06MA10', 'Resolver e elaborar problemas que envolvam adição ou subtração com números racionais positivos na representação fracionária.', 'Frações: significados (parte/todo, quociente), equivalência, comparação, adição e subtração; cálculo da fração de um número natural; adição e subtração de frações', 1);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 6, 'EF06MA11', 'Resolver e elaborar problemas com números racionais positivos na representação decimal, envolvendo as quatro operações fundamentais e a potenciação, por meio de estratégias diversas, utilizando estimativas e arredondamentos para verificar a razoabilidade de respostas, com e sem uso de calculadora.', 'Operações (adição, subtração, multiplicação, divisão e potenciação) com números racionais', 1);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 6, 'EF06MA12', 'Fazer estimativas de quantidades e aproximar números para múltiplos da potência de 10 mais próxima.', 'Aproximação de números para múltiplos de potências de 10', 1);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 6, 'EF06MA13', 'Resolver e elaborar problemas que envolvam porcentagens, com base na ideia de proporcionalidade, sem fazer uso da “regra de três”, utilizando estratégias pessoais, cálculo mental e calculadora, em contextos de educação financeira, entre outros.', 'Cálculo de porcentagens por meio de estratégias diversas, sem fazer uso da “regra de três”', 3);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (2, 6, 'EF06MA14', 'Reconhecer que a relação de igualdade matemática não se altera ao adicionar, subtrair, multiplicar ou dividir os seus dois membros por um mesmo número e utilizar essa noção para determinar valores desconhecidos na resolução de problemas.', 'Propriedades da igualdade', 4);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (2, 6, 'EF06MA15', 'Resolver e elaborar problemas que envolvam a partilha de uma quantidade em duas partes desiguais, envolvendo relações aditivas e multiplicativas, bem como a razão entre as partes e entre uma das partes e o todo.', 'Problemas que tratam da partição de um todo em duas partes desiguais, envolvendo razões entre as partes e entre uma das partes e o todo', 4);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (3, 6, 'EF06MA16', 'Associar pares ordenados de números a pontos do plano cartesiano do 1º quadrante, em situações como a localização dos vértices de um polígono.', 'Plano cartesiano: associação dos vértices de um polígono a pares ordenados', 2);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (3, 6, 'EF06MA17', 'Quantificar e estabelecer relações entre o número de vértices, faces e arestas de prismas e pirâmides, em função do seu polígono da base, para resolver problemas e desenvolver a percepção espacial.', 'Prismas e pirâmides: planificações e relações entre seus elementos (vértices, faces e arestas)', 4);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (3, 6, 'EF06MA18', 'Reconhecer, nomear e comparar polígonos, considerando lados, vértices e ângulos, e classificá-los em regulares e não regulares, tanto em suas representações no plano como em faces de poliedros.', 'Polígonos: classificações quanto ao número de vértices, às medidas de lados e ângulos e ao paralelismo e perpendicularismo dos lados', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (3, 6, 'EF06MA19', 'Identificar características dos triângulos e classificá-los em relação às medidas dos lados e dos ângulos.', 'Polígonos: classificações quanto ao número de vértices, às medidas de lados e ângulos e ao paralelismo e perpendicularismo dos lados', 3);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (3, 6, 'EF06MA20', 'Identificar características dos quadriláteros, classificá-los em relação a lados e a ângulos e reconhecer a inclusão e a intersecção de classes entre eles.', 'Polígonos: classificações quanto ao número de vértices, às medidas de lados e ângulos e ao paralelismo e perpendicularismo dos lados', 2);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (3, 6, 'EF06MA21', 'Construir figuras planas semelhantes em situações de ampliação e de redução, com o uso de malhas quadriculadas, plano cartesiano ou tecnologias digitais.', 'Construção de figuras semelhantes: ampliação e redução de figuras planas em malhas quadriculadas', 4);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (3, 6, 'EF06MA22', 'Utilizar instrumentos, como réguas e esquadros, ou softwares para representações de retas paralelas e perpendiculares e construção de quadriláteros, entre outros.', 'Construção de retas paralelas e perpendiculares, fazendo uso de réguas, esquadros e softwares', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (3, 6, 'EF06MA23', 'Construir algoritmo para resolver situações passo a passo (como na construção de dobraduras ou na indicação de deslocamento de um objeto no plano segundo pontos de referência e distâncias fornecidas etc.).', 'Construção de retas paralelas e perpendiculares, fazendo uso de réguas, esquadros e softwares', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (4, 6, 'EF06MA24', 'Resolver e elaborar problemas que envolvam as grandezas comprimento, massa, tempo, temperatura, área (triângulos e retângulos), capacidade e volume (sólidos formados por blocos retangulares), sem uso de fórmulas, inseridos, sempre que possível, em contextos oriundos de situações reais e/ou relacionadas às outras áreas do conhecimento.', 'Problemas sobre medidas envolvendo grandezas como comprimento, massa, tempo, temperatura, área, capacidade e volume', 2);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (4, 6, 'EF06MA25', 'Reconhecer a abertura do ângulo como grandeza associada às figuras geométricas.', 'Ângulos: noção, usos e medida', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (4, 6, 'EF06MA26', 'Resolver problemas que envolvam a noção de ângulo em diferentes contextos e em situações reais, como ângulo de visão.', 'Ângulos: noção, usos e medida', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (4, 6, 'EF06MA27', 'Determinar medidas da abertura de ângulos, por meio de transferidor e/ou tecnologias digitais.', 'Ângulos: noção, usos e medida', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (4, 6, 'EF06MA28', 'Interpretar, descrever e desenhar plantas baixas simples de residências e vistas aéreas.', 'Plantas baixas e vistas aéreas', 3);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (4, 6, 'EF06MA29', 'Analisar e descrever mudanças que ocorrem no perímetro e na área de um quadrado ao se ampliarem ou reduzirem, igualmente, as medidas de seus lados, para compreender que o perímetro é proporcional à medida do lado, o que não ocorre com a área.', 'Perímetro de um quadrado como grandeza proporcional à medida do lado', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (5, 6, 'EF06MA30', 'Calcular a probabilidade de um evento aleatório, expressando-a por número racional (forma fracionária, decimal e percentual) e comparar esse número com a probabilidade obtida por meio de experimentos sucessivos.', 'Cálculo de probabilidade como a razão entre o número de resultados favoráveis e o total de resultados possíveis em um espaço amostral equiprovável; Cálculo de probabilidade por meio de muitas repetições de um experimento (frequências de ocorrências e probabilidade frequentista)', 3);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (5, 6, 'EF06MA31', 'Identificar as variáveis e suas frequências e os elementos constitutivos (título, eixos, legendas, fontes e datas) em diferentes tipos de gráfico.', 'Leitura e interpretação de tabelas e gráficos (de colunas ou barras simples ou múltiplas) referentes a variáveis categóricas e variáveis numéricas', 2);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (5, 6, 'EF06MA32', 'Interpretar e resolver situações que envolvam dados de pesquisas sobre contextos ambientais, sustentabilidade, trânsito, consumo responsável, entre outros, apresentadas pela mídia em tabelas e em diferentes tipos de gráficos e redigir textos escritos com o objetivo de sintetizar conclusões.', 'Leitura e interpretação de tabelas e gráficos (de colunas ou barras simples ou múltiplas) referentes a variáveis categóricas e variáveis numéricas', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (5, 6, 'EF06MA33', 'Planejar e coletar dados de pesquisa referente a práticas sociais escolhidas pelos alunos e fazer uso de planilhas eletrônicas para registro, representação e interpretação das informações, em tabelas, vários tipos de gráficos e texto.', 'Coleta de dados, organização e registro; Construção de diferentes tipos de gráficos para representá-los e interpretação das informações', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (5, 6, 'EF06MA34', 'Interpretar e desenvolver fluxogramas simples, identificando as relações entre os objetos representados (por exemplo, posição de cidades considerando as estradas que as unem, hierarquia dos funcionários de uma empresa etc.).', 'Diferentes tipos de representação de informações: gráficos e fluxogramas', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 7, 'EF07MA01', 'Resolver e elaborar problemas com números naturais, envolvendo as noções de divisor e de múltiplo, podendo incluir máximo divisor comum ou mínimo múltiplo comum, por meio de estratégias diversas, sem a aplicação de algoritmos.', 'Múltiplos e divisores de um número natural', 1);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 7, 'EF07MA02', 'Resolver e elaborar problemas que envolvam porcentagens, como os que lidam com acréscimos e decréscimos simples, utilizando estratégias pessoais, cálculo mental e calculadora, no contexto de educação financeira, entre outros.', 'Cálculo de porcentagens e de acréscimos e decréscimos simples', 3);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 7, 'EF07MA03', 'Comparar e ordenar números inteiros em diferentes contextos, incluindo o histórico, associá-los a pontos da reta numérica e utilizá-los em situações que envolvam adição e subtração.', 'Números inteiros: usos, história, ordenação, associação com pontos da reta numérica e operações', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 7, 'EF07MA04', 'Resolver e elaborar problemas que envolvam operações com números inteiros.', 'Números inteiros: usos, história, ordenação, associação com pontos da reta numérica e operações', 3);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 7, 'EF07MA05', 'Resolver um mesmo problema utilizando diferentes algoritmos.', 'Fração e seus significados: como parte de inteiros, resultado da divisão, razão e operador', 2);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 7, 'EF07MA06', 'Reconhecer que as resoluções de um grupo de problemas que têm a mesma estrutura podem ser obtidas utilizando os mesmos procedimentos.', 'Fração e seus significados: como parte de inteiros, resultado da divisão, razão e operador', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 7, 'EF07MA07', 'Representar por meio de um fluxograma os passos utilizados para resolver um grupo de problemas.', 'Fração e seus significados: como parte de inteiros, resultado da divisão, razão e operador', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 7, 'EF07MA08', 'Comparar e ordenar frações associadas às ideias de partes de inteiros, resultado da divisão, razão e operador.', 'Fração e seus significados: como parte de inteiros, resultado da divisão, razão e operador', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 7, 'EF07MA09', 'Utilizar, na resolução de problemas, a associação entre razão e fração, como a fração 2/3 para expressar a razão de duas partes de uma grandeza para três partes da mesma ou três partes de outra grandeza.', 'Fração e seus significados: como parte de inteiros, resultado da divisão, razão e operador', 2);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 7, 'EF07MA10', 'Comparar e ordenar números racionais em diferentes contextos e associá-los a pontos da reta numérica.', 'Números racionais na representação fracionária e na decimal: usos, ordenação e associação com pontos da reta numérica e operações', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 7, 'EF07MA11', 'Compreender e utilizar a multiplicação e a divisão de números racionais, a relação entre elas e suas propriedades operatórias.', 'Números racionais na representação fracionária e na decimal: usos, ordenação e associação com pontos da reta numérica e operações', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (1, 7, 'EF07MA12', 'Resolver e elaborar problemas que envolvam as operações com números racionais.', 'Números racionais na representação fracionária e na decimal: usos, ordenação e associação com pontos da reta numérica e operações', 3);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (2, 7, 'EF07MA13', 'Compreender a ideia de variável, representada por letra ou símbolo, para expressar relação entre duas grandezas, diferenciando-a da ideia de incógnita.', 'Linguagem algébrica: variável e incógnita', 1);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (2, 7, 'EF07MA14', 'Classificar sequências em recursivas e não recursivas, reconhecendo que o conceito de recursão está presente não apenas na matemática, mas também nas artes e na literatura.', 'Linguagem algébrica: variável e incógnita', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (2, 7, 'EF07MA15', 'Utilizar a simbologia algébrica para expressar regularidades encontradas em sequências numéricas.', 'Linguagem algébrica: variável e incógnita', 2);
