INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (3, 9, 'EF09MA11', 'Resolver problemas por meio do estabelecimento de relações entre arcos, ângulos centrais e ângulos inscritos na circunferência, fazendo uso, inclusive, de softwares de geometria dinâmica.', 'Relações entre arcos e ângulos na circunferência de um círculo', 4);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (3, 9, 'EF09MA12', 'Reconhecer as condições necessárias e suficientes para que dois triângulos sejam semelhantes.', 'Semelhança de triângulos', 1);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (3, 9, 'EF09MA13', 'Demonstrar relações métricas do triângulo retângulo, entre elas o teorema de Pitágoras, utilizando, inclusive, a semelhança de triângulos.', 'Relações métricas no triângulo retângulo; Teorema de Pitágoras: verificações experimentais e demonstração; Retas paralelas cortadas por transversais: teoremas de proporcionalidade e verificações experimentais', 3);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (3, 9, 'EF09MA14', 'Resolver e elaborar problemas de aplicação do teorema de Pitágoras ou das relações de proporcionalidade envolvendo retas paralelas cortadas por secantes.', 'Relações métricas no triângulo retângulo; Teorema de Pitágoras: verificações experimentais e demonstração; Retas paralelas cortadas por transversais: teoremas de proporcionalidade e verificações experimentais', 3);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (3, 9, 'EF09MA15', 'Descrever, por escrito e por meio de um fluxograma, um algoritmo para a construção de um polígono regular cuja medida do lado é conhecida, utilizando régua e compasso, como também softwares.', 'Polígonos regulares', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (3, 9, 'EF09MA16', 'Determinar o ponto médio de um segmento de reta e a distância entre dois pontos quaisquer, dadas as coordenadas desses pontos no plano cartesiano, sem o uso de fórmulas, e utilizar esse conhecimento para calcular, por exemplo, medidas de perímetros e áreas de figuras planas construídas no plano.', 'Distância entre pontos no plano cartesiano', 3);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (3, 9, 'EF09MA17', 'Reconhecer vistas ortogonais de figuras espaciais e aplicar esse conhecimento para desenhar objetos em perspectiva.', 'Vistas ortogonais de figuras espaciais', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (4, 9, 'EF09MA18', 'Reconhecer e empregar unidades usadas para expressar medidas muito grandes ou muito pequenas, tais como distância entre planetas e sistemas solares, tamanho de vírus ou de células, capacidade de armazenamento de computadores, entre outros.', 'Unidades de medida para medir distâncias muito grandes e muito pequenas; Unidades de medida utilizadas na informática', 2);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (4, 9, 'EF09MA19', 'Resolver e elaborar problemas que envolvam medidas de volumes de prismas e de cilindros retos, inclusive com uso de expressões de cálculo, em situações cotidianas.', 'Volume de prismas e cilindros', 4);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (5, 9, 'EF09MA20', 'Reconhecer, em experimentos aleatórios, eventos independentes e dependentes e calcular a probabilidade de sua ocorrência, nos dois casos.', 'Análise de probabilidade de eventos aleatórios: eventos dependentes e independentes', 4);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (5, 9, 'EF09MA21', 'Analisar e identificar, em gráficos divulgados pela mídia, os elementos que podem induzir, às vezes propositadamente, erros de leitura, como escalas inapropriadas, legendas não explicitadas corretamente, omissão de informações importantes (fontes e datas), entre outros. ', 'Análise de gráficos divulgados pela mídia: elementos que podem induzir a erros de leitura ou de interpretação', 0);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (5, 9, 'EF09MA22', 'Escolher e construir o gráfico mais adequado (colunas, setores, linhas), com ou sem uso de planilhas eletrônicas, para apresentar um determinado conjunto de dados, destacando aspectos como as medidas de tendência central.', 'Leitura, interpretação e representação de dados de pesquisa expressos em tabelas de dupla entrada, gráficos de colunas simples e agrupadas, gráficos de barras e de setores e gráficos pictóricos', 3);
INSERT INTO tb_habilidades (id_unidade, ano_Escolar, sigla, descricao, objeto_Conhecimento, bimestre) VALUES (5, 9, 'EF09MA23', 'Planejar e executar pesquisa amostral envolvendo tema da realidade social e comunicar os resultados por meio de relatório contendo avaliação de medidas de tendência central e da amplitude, tabelas e gráficos adequados, construídos com o apoio de planilhas eletrônicas.', 'Planejamento e execução de pesquisa amostral e apresentação de relatório', 3);
